mapper.select:
SELECT redPointNo, memberNo, pointAmount, redPointCreateTime, redPointUpdateTime
FROM red_point  
WHERE memberNo = #{memberNo}

mapper.update:
update red_point	
set pointAmount=#{pointAmount}
where redPointNo= #{redPointNo}

mapper.insert:
	INSERT INTO member_bank_account (
			memberNo
			,bankName
			,accountOwner
			,bankAccount
			,accountStatus
			,statusMessage
			,regAdminNo
			,regType
			,createTime
			,updateTime
	) VALUES (
			#{memberNo}
			,#{bankName}
			,#{accountOwner}
			,#{bankAccount}
			,#{accountStatus}
			,#{statusMessage}
			,#{regAdminNo}
			,#{regType}
			,NOW()
			,NOW()	
	)
   
	    SELECT LAST_INSERT_ID()

mapper.update:
UPDATE point_withdrawal
memberNo=#{memberNo}, memberBankAccountNo=#{memberBankAccountNo}, withdrawalAmount=#{withdrawalAmount}, withdrawalStatus=#{withdrawalStatus}, withdrawalPointType=#{withdrawalPointType}, withdrawalMessage=#{withdrawalMessage}, updateTime=NOW()
AND pointWithdrawalNo= #{pointWithdrawalNo} and memeberNo = #{memberNo,jdbcType=INTEGER}
