mapper.select:
select 

from gift_card_issue
where giftCardIssueNo = #{giftCardIssueNo,jdbcType=INTEGER}
  and pinNumber = #{pinNumber,jdbcType=VARCHAR}

mapper.delete:
delete from gift_card_issue
where giftCardIssueNo = #{giftCardIssueNo,jdbcType=INTEGER}
  and pinNumber = #{pinNumber,jdbcType=VARCHAR}
