mapper.select:
select
from green_point as GP inner join member as M on ( GP.memberNo = M.memberNo )
where 1 = 1

select
from red_point as RP inner join member as M on ( RP.memberNo = M.memberNo )
where 1 = 1

set @node_type := #{node_type}; set @node_no := #{node_no}; set @affiliate_no := #{affiliate_no};
set @node_id := concat(case when @node_type < 3 then '5' else @node_type end,'-',case when @node_type < 3 then @affiliate_no else @node_no end); set @L := 0;
select node_type,node_no,up_node_no,recommenderNo, saleManagerNo, memberNo, recommenderMemberNo,saleManagerMemberNo from( select * from node_tree where node_id in( select _node_id from( SELECT @node_id AS _node_id, (SELECT @node_id := up_node_id FROM node_tree WHERE node_id = _node_id) AS upper_id, (@L := @L + 1) AS depth FROM node_tree AS h WHERE @node_id is not null )a ) union all select '1' node_type, concat('1-', a.memberNo) node_id, concat('5-',@affiliate_no) up_node_id,a.memberNo, @affiliate_no, memberName name , a.recommenderNo, b.saleManagerNo, a.memberNo, b.memberNo as recommenderMemberNo,c.memberNo saleManagerMemberNo from member a left outer join recommender b on a.recommenderNo = b.recommenderNo left outer join sale_manager c on b.saleManagerNo = c.saleManagerNo where a.memberNo=@node_no and @node_type = 1
union all
select '2' node_type, concat('2-', a.recommenderNo) node_id, concat('5-',@affiliate_no) up_node_id,a.recommenderNo, @affiliate_no, b.memberName name , null, a.saleManagerNo , a.memberNo, null as recommenderMemberNo, c.memberNo saleManagerMemberNo from recommender a left outer join member b on a.memberNo = b.memberNo left outer join sale_manager c on a.saleManagerNo = c.saleManagerNo where a.recommenderNo=@node_no and @node_type = 2 )a

select
from affiliate_tid as AT inner join affiliate as A on (AT.affiliateNo= A.AffiliateNo)
where 1 = 1

 select 
	 paymentRouterNo, paymentRouterType, paymentRouterCode, paymentRouterName
 from payment_router
 where 
  	 1 = 1 
 	 AND paymentRouterNo = #{paymentRouterNo,jdbcType=INTEGER}
     AND paymentRouterType = #{paymentRouterType,jdbcType=VARCHAR}
     AND paymentRouterCode = #{paymentRouterCode,jdbcType=VARCHAR}
     AND paymentRouterName = #{paymentRouterName,jdbcType=VARCHAR}
